mapper.select:
select compare.id,serial_number,order_id,create_time,fee,order_log.status from compare
    left join order_log on compare.order_id=order_log.id
where (serial_number ='${param1}' or order_log.status='${param1}');
                         and (create_time between '${param2}' and '${param3}') limit ${param4},${param5};

mapper.delete:
delete order_log,compare from compare
  left join order_log on compare.order_id=order_log.id
  where compare.order_id='${param1}';
